import{_ as t,c as a,o as e,b as r}from"./app.b8b8de1c.js";const v='{"title":"HTML \u4E2D\u7684 JavaScript","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4F7F\u7528 <script> \u5143\u7D20","slug":"\u4F7F\u7528-script-\u5143\u7D20"},{"level":2,"title":"\u884C\u5185\u811A\u6B65\u4E0E\u5916\u90E8\u811A\u672C\u7684\u6BD4\u8F83","slug":"\u884C\u5185\u811A\u6B65\u4E0E\u5916\u90E8\u811A\u672C\u7684\u6BD4\u8F83"},{"level":2,"title":"\u6587\u6863\u6A21\u5F0F\u5BF9 JavaScript \u6709\u4EC0\u4E48\u5F71\u54CD","slug":"\u6587\u6863\u6A21\u5F0F\u5BF9-javascript-\u6709\u4EC0\u4E48\u5F71\u54CD"},{"level":2,"title":"\u786E\u4FDD JavaScript \u4E0D\u53EF\u7528\u65F6\u7684\u7528\u6237\u4F53\u9A8C","slug":"\u786E\u4FDD-javascript-\u4E0D\u53EF\u7528\u65F6\u7684\u7528\u6237\u4F53\u9A8C"}],"relativePath":"book/professional_javascript_for_web_developers/chapter_2.md","lastUpdated":1637245399084}',p={},c=r('<h1 id="html-\u4E2D\u7684-javascript" tabindex="-1">HTML \u4E2D\u7684 JavaScript <a class="header-anchor" href="#html-\u4E2D\u7684-javascript" aria-hidden="true">#</a></h1><h2 id="\u4F7F\u7528-script-\u5143\u7D20" tabindex="-1">\u4F7F\u7528 &lt;script&gt; \u5143\u7D20 <a class="header-anchor" href="#\u4F7F\u7528-script-\u5143\u7D20" aria-hidden="true">#</a></h2><p>\u5217\u4E3E 8 \u4E2A\u5C5E\u6027</p><p>src\uFF1A\u8BBE\u7F6E\u540E\u6D4F\u89C8\u5668\u4F1A\u5FFD\u7565\u6807\u7B7E\u5185\u90E8\u4EE3\u7801\uFF0C\u4E0D\u53D7\u540C\u6E90\u7B56\u7565\u7684\u9650\u5236\uFF0C\u4F46\u662F https \u4E0B\u4E0D\u80FD\u8BF7\u6C42 http \u670D\u52A1\u5668\u4E0B\u7684\u8D44\u6E90</p><p>type\uFF1A\u6309\u60EF\u4F8B\uFF0C\u8FD9\u4E2A\u503C\u59CB\u7EC8\u662F text/javascript \u3002\u5F53\u503C\u4E3A &quot;module&quot; \u65F6\uFF0C\u4EE3\u7801\u4F1A\u88AB\u5F53\u6210 ES6 \u6A21\u5757\uFF0C\u8BBE\u7F6E\u6D4F\u89C8\u5668\u4E0D\u8BC6\u522B\u7684\u503C\uFF0C\u4F1A\u5FFD\u7565\u89E3\u6790\u5176\u5185\u5BB9</p><p>async: \u7ACB\u5373\u4E0B\u8F7D\u811A\u672C\uFF0C\u4E0D\u963B\u585E\u5176\u4ED6\u8D44\u6E90\u4E0B\u8F7D\uFF0C\u4E0D\u963B\u585E\u6587\u6863\u89E3\u6790\uFF0C\u4E0B\u8F7D\u5B8C\u6210\u540E\u7ACB\u5373\u6267\u884C\u3002\u5BF9\u5916\u90E8\u811A\u672C\u6709\u6548</p><p>defer\uFF1A\u4E0B\u8F7D\u811A\u672C\uFF0C\u5F85\u6587\u6863\u5B8C\u5168\u88AB\u89E3\u6790\u540E\u6267\u884C\uFF0C\u5BF9\u5916\u90E8\u811A\u672C\u6709\u6548</p><p>integrity\uFF1A\u6307\u5B9A\u52A0\u5BC6\u7B7E\u540D\uFF0C\u81EA\u52A8\u6821\u9A8C\u63A5\u53D7\u7684\u811A\u6B65\u8D44\u6E90\u662F\u5426\u7B7E\u540D\u4E00\u81F4\u3002\u786E\u4FDD\u5185\u5BB9\u5206\u53D1\u7F51\u7EDC\u4E0D\u4F1A\u63D0\u4F9B\u6076\u610F\u5185\u5BB9</p><p>crossorigin\uFF1A\u9ED8\u8BA4\u4E0D\u4F7F\u7528 CORS\u3002 \u503C\u4E3A &quot;anonymous&quot; \u8BF7\u6C42\u4E0D\u5305\u542B\u51ED\u636E\uFF0C\u503C\u4E3A &quot;use-credentials&quot; \u610F\u5473\u7740\u8BF7\u6C42\u4F1A\u5305\u542B\u51ED\u636E</p><p>charset\uFF1A\u6307\u5B9A\u4EE3\u7801\u5B57\u7B26\u96C6\uFF0C\u5927\u591A\u5C11\u6D4F\u89C8\u5668\u4E0D\u5728\u4E4E\u5B83\u7684\u503C</p><p>language\uFF1A\u5E9F\u5F03\uFF0C\u5927\u591A\u5C11\u6D4F\u89C8\u5668\u4F1A\u5FFD\u7565\u6B64\u5C5E\u6027</p><h2 id="\u884C\u5185\u811A\u6B65\u4E0E\u5916\u90E8\u811A\u672C\u7684\u6BD4\u8F83" tabindex="-1">\u884C\u5185\u811A\u6B65\u4E0E\u5916\u90E8\u811A\u672C\u7684\u6BD4\u8F83 <a class="header-anchor" href="#\u884C\u5185\u811A\u6B65\u4E0E\u5916\u90E8\u811A\u672C\u7684\u6BD4\u8F83" aria-hidden="true">#</a></h2><p>\u5BF9\u4E8E HTML \u4E2D\u6CA1\u6709\u8BBE\u7F6E async defer \u7684 &lt;script&gt; \uFF0C\u6D4F\u89C8\u5668\u4F1A\u4F9D\u6B21\u89E3\u6790\uFF08\u5916\u90E8\u811A\u672C\u5219\u7B49\u5F85\u4E0B\u8F7D\u5B8C\u518D\u89E3\u6790\uFF09\uFF0C\u671F\u95F4\u4F1A\u505C\u6B62\u5BF9 DOM CSS \u7684\u89E3\u6790\uFF08\u963B\u585E\uFF09</p><p>\u8BBE\u7F6E\u4E86 defer \u5C5E\u6027\u540E\uFF0C\u6D4F\u89C8\u5C31\u4F1A\uFF08\u63A8\u8FDF\uFF09\u5728\u6587\u6863\u89E3\u6790\u5B8C\u540E\uFF0CDOMContentLoaded \u4E8B\u4EF6\u4E4B\u524D\u6267\u884C\u3002\u5728\u5B9E\u9645\u4E2D defer \u7684\u811A\u6B65\u4E5F\u4E0D\u7EDD\u5BF9\u603B\u662F\u6309\u987A\u5E8F\u6267\u884C\u6216\u8005\u5728 \u5C5E\u6027\u540E\uFF0C\u6D4F\u89C8\u5C31\u4F1A\uFF08\u63A8\u8FDF\uFF09\u5728\u6587\u6863\u89E3\u6790\u5B8C\u540E\uFF0CDOMContentLoaded \u4E8B\u4EF6\u4E4B\u524D\u6267\u884C</p><p>\u8BBE\u7F6E\u4E86 async \u7684\u811A\u6B65\uFF0C\u4F1A\u4FDD\u8BC1\u5728\u9875\u9762\u7684 load \u4E8B\u4EF6\u4E4B\u524D\u6267\u884C\uFF0C\u4F46\u76F8\u5BF9\u4E8E DOMContentLoaded \u524D\u540E\u5747\u53EF\u80FD\uFF0Casync \u7684\u811A\u6B65\u4E4B\u95F4\u65F6\u65E0\u6CD5\u4FDD\u8BC1\u6267\u884C\u987A\u5E8F\u7684</p><p>\u4F7F\u7528 document.createEelement(&#39;script&#39;) \u52A8\u6001\u521B\u5EFA\u7684\u811A\u672C\uFF0C\u5728\u6DFB\u52A0\u5230 DOM \u4E4B\u524D\u4E0D\u4F1A\u53D1\u9001\u8BF7\u6C42\uFF0C\u811A\u672C\u4E5F\u662F\u4E00\u672C\u52A0\u8F7D\u7684\uFF0C\u8FD9\u548C async \u5C5E\u6027\u4F5C\u7528\u7C7B\u4F3C\u3002\u4F46\u662F\u8FD9\u4F1A\u5F71\u54CD\u5B83\u5728\u8D44\u6E90\u83B7\u53D6\u961F\u5217\u4E2D\u7684\u4F18\u5148\u7EA7\uFF0C\u53EF\u80FD\u4F1A\u5F71\u54CD\u6027\u80FD\u3002\u53EF\u4EE5\u4F7F\u7528 <code>&lt;link rel=&quot;preload&quot; href=&quot;xx.js&quot;&gt;</code> \u8BA9\u9884\u52A0\u8F7D\u5668\u77E5\u9053\u8FD9\u4E9B\u52A8\u6001\u8BF7\u6C42\u6587\u4EF6\u7684\u5B58\u5728</p><p>\u5916\u90E8 script \u66F4\u6613\u4E8E\u7EF4\u62A4\u548C\u5206\u53D1\uFF0C\u540C\u65F6\u53EF\u88AB http \u7F13\u5B58\uFF0C\u8F7B\u91CF\u72EC\u7ACB\u7684 JavaScript \u7EC4\u4EF6\u5728 http2 \u4E0B\u8BF7\u6C42\u6D88\u8017\u5DF2\u663E\u8457\u964D\u4F4E\uFF0C\u5982\u679C\u4E0D\u60F3\u652F\u6301\u8001\u7684\u6D4F\u89C8\u5668\uFF0C\u8FD9\u662F\u4E2A\u4E0D\u9519\u7684\u9009\u62E9</p><p>\u628A\u5916\u90E8\u52A0\u8F7D\u7684 script \u653E\u5728 &lt;body&gt; \u6807\u7B7E\u5185\u7684\u6700\u5E95\u90E8\uFF0C\u4F9D\u7136\u662F\u6700\u4F73\u5B9E\u8DF5</p><h2 id="\u6587\u6863\u6A21\u5F0F\u5BF9-javascript-\u6709\u4EC0\u4E48\u5F71\u54CD" tabindex="-1">\u6587\u6863\u6A21\u5F0F\u5BF9 JavaScript \u6709\u4EC0\u4E48\u5F71\u54CD <a class="header-anchor" href="#\u6587\u6863\u6A21\u5F0F\u5BF9-javascript-\u6709\u4EC0\u4E48\u5F71\u54CD" aria-hidden="true">#</a></h2><p>IE5.5 \u53D1\u660E\u4E86\u6587\u6863\u6A21\u5F0F\u7684\u6982\u5FF5\uFF0C\u53EF\u4F7F\u7528 doctype \u5207\u6362\u6587\u6863\u6A21\u5F0F\u3002\u6700\u521D\u6709\u4E24\u79CD\uFF1A\u6DF7\u6742\u6A21\u5F0F\uFF08quirks mode\uFF09\u548C\u6807\u51C6\u6A21\u5F0F\uFF08standard mode\uFF09</p><p>\u5728 quirk mode \u4E0B\u652F\u6301\u4E00\u4E0B\u975E\u6807\u51C6\u7684\u7279\u6027\uFF0C\u4E3B\u8981\u4F53\u73B0\u5728 CSS \u6E32\u67D3\u65B9\u9762\uFF0CJavaScript \u4E5F\u6709\u4E00\u5B9A\u5173\u8054\u5F71\u54CD\uFF08\u526F\u4F5C\u7528\uFF09</p><p>\u968F\u7740\u6D4F\u89C8\u5668\u7684\u666E\u53CA\uFF0C\u51FA\u73B0\u4E86\u51C6\u6807\u51C6\u6A21\u5F0F\uFF08almost standard mode\uFF09\uFF0C\u652F\u6301\u5F88\u591A\u6807\u51C6\u7279\u6027\u7684\u540C\u65F6\uFF0C\u53C8\u6CA1\u6709\u90A3\u4E48\u4E25\u683C\u3002\u3002\u3002</p><h2 id="\u786E\u4FDD-javascript-\u4E0D\u53EF\u7528\u65F6\u7684\u7528\u6237\u4F53\u9A8C" tabindex="-1">\u786E\u4FDD JavaScript \u4E0D\u53EF\u7528\u65F6\u7684\u7528\u6237\u4F53\u9A8C <a class="header-anchor" href="#\u786E\u4FDD-javascript-\u4E0D\u53EF\u7528\u65F6\u7684\u7528\u6237\u4F53\u9A8C" aria-hidden="true">#</a></h2><p>\u5728\u4E0D\u652F\u6301 JavaScript \u6216\u5173\u95ED JavaScript \u529F\u80FD\u7684\u6D4F\u89C8\u5668\u4E0A\uFF0C\u4F1A\u89E3\u6790 &lt;noscript&gt; \u6807\u7B7E\u7684\u5185\u5BB9\u3002\u8FD9\u6837\u53EF\u63D0\u9192\u7528\u6237\u9875\u9762\u4F9D\u8D56 JavaScript \u624D\u80FD\u6B63\u5E38\u5DE5\u4F5C</p>',24),i=[c];function s(d,o,n,l,h,_){return e(),a("div",null,i)}var S=t(p,[["render",s]]);export{v as __pageData,S as default};
